@Injectable({
  providedIn: 'root'
})

export class ProductListService {

  constructor(private http: HttpClient) { }


  /**
   * Servicio para obetener los productos que coincidan con el termino de busqueda
   * @param query Termino de busqueda
   * @returns Devuelve el objeto con la lista de productos
   */
  getProductList(query: string): Observable<any> {
    let queryParams = new HttpParams();
    queryParams = queryParams.append('q', query);
    return this.http.get('http://localhost:3000/sites/MLA/search', { params: queryParams });
  }
}
